mapper.select:
SELECT mgEndYMD 
FROM globalbiz.MagamDate 
WHERE jisaCD = #{jisaCD} AND mgMonth = #{currentYYYYMM}

SELECT cHoursStart, cHoursEnd 
FROM globalbiz.DeptMst 
WHERE jisaCD = #{jisaCD} AND deptCD = #{deptCD}

SELECT * 
FROM globalbiz.CodeDtl 
WHERE jisaCD = #{jisaCD} AND mstCD = #{mstCD} AND dtlCD BETWEEN #{deptHour.cHoursStart} AND #{deptHour.cHoursEnd}

SELECT gFstName, gLstName, city, stateCD, zip, addr, gEmail, gPhone, gCellPhone
FROM globalbiz.MemMst 
WHERE memKey = #{memKey}
