mapper.select:
SELECT a.memKey, a.mFstName, a.mLstName, a.mBirthDay, a.gradeCD, globalbiz.FuncCodeDtlNM(b.jisaCD,'0003',a.gradeCD,'','Y') AS gradeName, a.schoolName, a.mEmail, a.eContact, a.ePhone, a.remarks, a.regDate, MIN(b.StatusCD) AS statusCD, globalbiz.FuncCodeDtlNM(b.jisaCD,'0008',MIN(b.statusCD),'','Y') AS statusName FROM globalbiz.MemMst AS a INNER JOIN globalbiz.MemSubjMst AS b ON a.memKey = b.memKey WHERE gFstName = #{gFstName} AND gLstName = #{gLstName} AND b.jisaCD = #{jisaCD}
AND b.deptCD = #{deptCD}
GROUP BY a.memKey ORDER BY a.memKey

SELECT a.memKey, a.subj, a.statusCD, b.yoil, globalbiz.FuncCodeDtlNM(a.jisaCD, '0006', b.yoil, 'E', 'Y') AS yoilName, a.registFstYMD, a.registFnlYMD, CASE a.registFnlYMD WHEN '' THEN a.registFstYMD ELSE a.registFnlYMD END AS recentRegistFnlYMD, DATE_FORMAT(CASE a.registFnlYMD WHEN '' THEN a.registFstYMD ELSE a.registFnlYMD END, '%m/%d/%Y') AS convertRegistYMD, a.dropFnlYMD, IFNULL(DATE_FORMAT(a.dropFnlYMD, '%m/%d/%Y'), '') AS convertDropYMD, b.visitHours, globalbiz.FuncCodeDtlNM(a.jisaCD, '0206' , b.visitHours, '', 'Y') AS visitHourName, c.digYN, a.jisaCD, IFNULL(d.omrDate, '') AS omrDate, IFNULL(e.sdyymm, '') AS sdyymm FROM globalbiz.MemSubjMst AS a INNER JOIN globalbiz.MemSubjStudy AS b ON a.memKey = b.memKey AND a.subj = b.subj INNER JOIN globalbiz.DeptSubjInfo AS c ON a.jisaCD = c.jisaCD AND a.deptCD = c.deptCD AND a.subj = c.subj LEFT JOIN ( SELECT hkey, kwamok, MAX(omrDate) AS omrDate FROM globaldig.OmrGicho AS aa GROUP BY hkey, kwamok ORDER BY omrDate DESC ) AS d ON a.memKey = d.hkey AND a.subj = d.kwamok LEFT JOIN ( SELECT hkey, kwamok, MAX(CONCAT(sdyy, sdmm)) AS sdyymm FROM globaldig.SDGicho AS bb GROUP BY hkey, kwamok ORDER BY sdyy DESC, sdmm DESC ) AS e ON a.memKey = e.hkey AND a.subj = e.kwamok WHERE a.memKey = #{memKey} AND a.jisaCD = #{jisaCD}
AND b.deptCD = #{deptCD}
ORDER BY statusCD, subj DESC

